<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Content extends MY_Controller {    public function __construct() {        parent::__construct();		    }    public function index($page_slug) { 			$this->pagewisecontent($page_slug);		$data = $this->data;				$data['view'] = 'pages/details';        $this->load->view('templates/home', $data);    }}